$(document).ready(()=>{$(".btn-filter-application").unbind().on("click",function(t){t.preventDefault();const a=$(".application-content"),i=$(".application-filter");let e=$(this).data("filter");i.find("a").removeClass("active"),$(this).addClass("active"),"*"==e?a.find(".card").fadeIn():(a.find(".card").fadeOut(),setTimeout(()=>{a.find('.card[data-type="'+e+'"]').fadeIn()},100))})});